import React, { useState } from "react";
import "./index.css"
const App =()=>{
    
    const[name,setName] = useState()
    const[lastName,setLastName] =useState()
    const[FName,setFname] = useState()
    const[LName,setLname] =useState()

    const[email,setEmail]=useState()
    const[eName,seteName]=useState()
    const runAgain=(event)=>{
        event.preventDefault();
        setFname(name);
        setLname(lastName);
        seteName(email);

    }
    const run=(e)=>{
      setName(e.target.value)
    }
    const runtwo=(e)=>{
        setLastName(e.target.value)

    }
    const runemail =(e)=>{
        setEmail(e.target.value)
    }
    
    return(
        <>
        <form onSubmit={runAgain}>
        <div>
       
      
            <h1>{FName} {LName} </h1><br /><span>{eName} </span> 
            <input required onChange={run} type="text" placeholder="Enter Your Name" defaultValue="" value={name}/>

            <input required onChange={runtwo} type="text" placeholder="Enter Your Last Name" defaultValue="" value={lastName}/>

            <input type="email" id="run" required onChange={runemail} placeholder="Enter Your Gmail" defaultValue="" value={email}/>
            <button type="submit">click me ðŸŒ¸</button>
        </div>
        </form>
        </>
    )
}
export default App
