import React, { useState } from "react";
import './index.css';

const App = () => {
    const [activity, setActivity] = useState('');
    const [listData, setListData] = useState([]);
    const [editIndex, setEditIndex] = useState(-1);

    function addActivity() {
        if (activity.trim() !== '') {
            if (editIndex !== -1) {
                // Editing an existing activity
                setListData((prevListData) => {
                    const updatedList = [...prevListData];
                    updatedList[editIndex] = activity;
                    setActivity('');
                    setEditIndex(-1);
                    return updatedList;
                });
            } else {
                // Adding a new activity
                setListData((prevListData) => {
                    const updatedList = [...prevListData, activity];
                    setActivity('');
                    return updatedList;
                });
            }
        }
    }

    function removeActivity(index) {
        setListData((prevListData) => {
            const updatedList = prevListData.filter((_, i) => i !== index);
            return updatedList;
        });
    }

    function editActivity(index) {
        const activityToEdit = listData[index];
        setActivity(activityToEdit);
        setEditIndex(index);
    }

    function removeAll() {
        setListData([]);
    }

    return (
        <>
            <div className="container">
                <header>
                    <div>
                        <h1>To-Do-List</h1>
                    </div>
                    <input type="text" placeholder="Add Activity" value={activity} onChange={(e) => setActivity(e.target.value)} />
                    <button className="btn" onClick={addActivity}>{editIndex !== -1 ? "Update" : "Add"}</button>
                    {listData.length >= 2 && <button className="removeall" onClick={removeAll}>Remove All</button>}
                </header>
                {listData.map((data, index) => (
                    <div className="list-item" key={index}>
                        <div className="listdata">{data}</div>
                        <div className="list-actions">
                            <button className="editbtn" onClick={() => editActivity(index)}>Edit</button>
                            <button className="delbtn" onClick={() => removeActivity(index)}>Delete</button>
                        </div>
                    </div>
                ))}
            </div>
        </>
    );
}

export default App;
