    const[Lname,setLname] =useState()
    const runAgain=(event)=>{
        event.preventDefault();
        setFname(name)
    }
    const run=(e)=>{
      setName(e.target.value)
    }
    const runtwo=(e)=>{
        setLastName(e.target.value)

    }
    
    return(
        <>
        <form onSubmit={runAgain}>
        <div>
            <h1>Hello {fullName}</h1>
